import React,{ useState } from "react";
import {  useDispatch } from "react-redux";
import {useSelector} from "react-redux";

const CartDetails = () => {

    const cartDesc = useSelector(state => state.addtocartt.addtocartItems);
    const [quantities, setQuantities] = useState(cartDesc.map(() => 0));
    const dispatch = useDispatch();

    const handleIncrement = (index) => {
        const newQuantities = [...quantities];
        newQuantities[index]++;
        setQuantities(newQuantities);
    }

    const handleDecrement = (index) => {
        const newQuantities = [...quantities];
        if (newQuantities[index] > 0) {
            newQuantities[index]--;
            setQuantities(newQuantities);
        }
    }

    const getTotalPrice = (index) => {
        return cartDesc[index].price * quantities[index];
    }


    return(
      <div style={{width:"100%",height:"100%"}}>
        <div style={{width:"75%",height:500,backgroundColor: '#fff',margin:40}}>
        <h3 style={{fontSize:20,margin:10,padding:20}}>Shopping Cart</h3>
        <hr style={{}}></hr>
            {
                cartDesc.map((item,index) => (
                   <div key={index} style={{ height: 300,  flexDirection: "row", alignItems: 'center', }}>
                    <img style={{ height: 100, width: 100 }} src={item.thumbnail} alt="pp"/>
                    <div style={{  }}>
                        <span style={{ fontSize: 10, color: "#000" }}>{item.description}</span>
                        <span style={{ fontSize: 10, color: "#000" }}>{item.title}</span>
                        <div style={{ width: 260, alignItems: "center", justifyContent: "space-between", alignItems: "center", flexDirection: "row" }}>
                          <div  style={{ height: 30, width: 70, justifyContent: "space-between", flexDirection: "row", alignItems: "center" }}>
                            <span style={{ fontSize: 10, color: "#000" }}>₹ {item.price}</span>
                          </div>
                          <span style={{ color: "#000" }}>Total Price :</span>
                          <span style={{ fontSize: 10, color: "#000" }}>₹ {getTotalPrice(index)}</span>
                          <div style={{ height: 30, width: 70, alignItems: "center", paddingHorizontal: 3, justifyContent: "space-between", borderWidth: 0.5, borderRadius: 5, borderColor: "#e6e6e6", flexDirection: "row" }}>
                            <button onClick={()=>handleDecrement(index)} style={{ fontSize: 28, color: "#000" }}>-</button>
                            <span style={{ fontSize: 13, color: "#000" }}>{quantities[index]}</span>
                            <button onClick={()=> handleIncrement(index)} style={{ fontSize: 22, color: "#000" }}>+</button>
                          </div>
                        </div>

                    </div>
                   </div> 
                ) 
                )
            }
        </div>
      </div>
    )
}
export default CartDetails;
