import { Axios } from "axios";
import React, { useEffect, useState } from "react";


const Cart = () =>  {


    const[searchquery,setsearchquery] = useState("");
    const[product,setproduct] = useState([]);

    const fetchProducts = async () => {
        try{
            const response = await Axios.get("https://dummyjson.com/products?search=${searchquery}");
            const filterProducts = response.data.products.filter(product =>
            product.title.toLowerCase().includes(searchquery.toLowerCase()))
            console.log(response.data.products);
            setproduct(filterProducts);
        } catch(error) 
        {
          console.error("errorr",error);
        }
    }

    useEffect(() => {
        if(searchquery.length)
        fetchProducts()
    })
    return(
        <div>
        {
          product.map((item) => (
            <button key={item.id} style={{width:300,marginLeft:170,marginTop:40,height:400,border:"none",borderStyle:"groove",backgroundColor:"white",borderRadius:15}}> 
            <span style={{fontSize:25}}>{item.title}</span>
            </button>
          ))

          }
        
        </div>
        
    )
}

export default Cart;
