 import { useLocation} from 'react-router-dom'
 // import { addToCart } from "../../redux/action";
 //import {  useDispatch } from "react-redux";
//import { addToCart } from "../../redux/action";
//import { useParams } from 'react-router-dom';
// import user from '../../assets/user.png'


 const Product = () => { 

  
  
   const location = useLocation();
   // Extract the item data from location state
   const { item } = location.state; 
     console.log(item);

   

//const dispatch = useDispatch();

//      const passData = (item) => {
//      dispatch(addToCart(item))
//}



     return(

    // <div>
    //   <img style={{ height: 320, width: "78%", borderWidth: 1, borderColor: "#000"}} src={item.thumbnail} alt="lll"/>
    // </div>
        
        <div>
         <div style={{height:350,width:100,borderRadius:10,marginTop:30}}>
        <img src={item.thumbnail} alt="kk"/>
        </div> 
        <div>
          {item.images.map((image,index) => (
            <div key={index} style={{ }}>
            <img style={{height: 45, width: 45}} src={image} alt="q"/>
            </div>
          )
          )}
        </div>
         <div style={{backgroundColor: "#fff", width: "100%", height: "100%"}} >
          <div>
            <span style={{}}>Creative Projection Electronics Clock LED Digital {"\n"} {item.description}</span>
            <span style={{  }}>STORE:INFINITY WORKS</span>
            <div style={{}}>
              <div style={{  }}>
                <span style={{}}>{item.price}</span>
                <span style={{}}>Save upto 67%</span>
              </div>
              <span  style={{  }}>Get Coupon</span>
            </div>
            <div style={{ flexDirection: "row", justifyContent: "space-between" }}>
              <div>
                <div>
                  <span style={{  }}>Price:</span>
                  <span style={{  }}>{item.price}</span>
                  <span style={{  }}>(Exclusive of all taxes)</span>
                </div>
                <div>
                  <span style={{  }}>{item.price}</span>
                  <span  style={{  }}>/Piece</span>
                  <span style={{ }}>(Inclusive of all taxes)</span>
                </div>
             </div>
             <div  style={{flexDirection: "row", width: "19%", height: "70%",alignItems: "center",justifyContent: "space-between"}}>
              <img style={{  }} src="" alt="lll"/>
              <img style={{  }} src="" alt="kkk"/>
             </div>
            </div>
          </div>
          <div>
            <button style={{height:50, width: 360, borderRadius: 30,alignItems: "center",justifyContent: "center",backgroundColor: "red"}}>Add to cart</button>
          </div>
        </div> 
        </div>
     )

 }
 export default Product;
