import { useNavigate } from "react-router";


 const Product = () => { 

  const nav = useNavigate();
  
   const location = useLocation();
   // Extract the item data from location state
   const { item } = location.state;
     console.log(item);

   

const dispatch = useDispatch();

      const passData = (item) => {
      dispatch(addToCart(item))
      nav('/cart')
}



